/**
 * Task 5: react useEffect hook
 * th2: useEffect(callback,[])
 */
import { Routes, Route, Link } from "react-router-dom";
import { useFetch } from "../services/useFetch";
import { Task7 } from "./Task7";
export const Task6 = () => {
    const { data, isErrors, isLoading } = useFetch('https://jsonplaceholder.typicode.com/posts')
    return (
        <>
            {isErrors && <div>Sever internal errors</div>}
            {isLoading && <div>Please waiting for munutes</div>}
            {data && data.length > 0 && (
                <ul>
                    {data.map(post => (
                        <li>
                            <Link to={`/task-7/${post.id}`} key={post.id}>{post.title}</Link>
                        </li>
                    ))}
                </ul>
            )}
            <Routes>
                <Route path="/task-7/:id" element={<Task7 />} />
            </Routes>
        </>
    )
}
