import { useState } from "react";
export const Task1 = () => {
    const [count, setCount] = useState(0);
    const increment = () => {
        setCount(count + 1);
    }
    const reduced = (count) => {
        if (count > 0) {
            setCount(count - 1);
        }
    }
    return (
        <>
            count : {count}
            <div>
                <button onClick={increment}>increment</button>
                <button onClick={reduced}>reduced</button>
            </div>
        </>
    )
}
