/**
 * Task 5: react useEffect hook
 * th2: useEffect(callback,[])
 */
import { useEffect, useState } from "react"
import { useFetch } from "../services/useFetch";
export const Task6 = () => {
    const { data: posts, isErrors, isLoading } = useFetch('https://jsonplaceholder.typicode.com/posts')
    return (
        <>
            <hr />
            {isErrors && <div>Sever internal errors</div>}
            {isLoading && <div>Please waiting for munutes</div>}
            {posts.length > 0 && (
                <ul>
                    {posts.map(post => (
                        <li key={post.id}>{post.title}</li>
                    ))}
                </ul>
            )}
        </>
    )
}
