/**
 * Task 5: react useEffect hook
 * th2: useEffect(callback,[])
 */
import { useEffect, useState } from "react"
export const Task6 = () => {
    const [posts, setpost] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [isErrors, setIsErros] = useState(false);
    useEffect(() => {
        try {
            setIsLoading(true);
            fetch('https://jsonplaceholder.typicode.com/posts5')
                .then(res => res.json())
                .then(posts => {
                    setpost(posts);
                    setIsLoading(false);
                });
        } catch (error) {
            console.log(error);
            setIsErros(true);
            setIsLoading(false);
        }
    }, []);
    return (
        <>
            <hr />
            <ul>
                {isLoading && <div>Please waiting for munutes</div>}
                {isErrors && <div>Sever internal errors</div>}
                {isLoading === false && posts.map(item => (
                    <li key={item.id}>
                        {item.title}
                    </li>
                ))}

            </ul>
        </>
    )
}
