import { useParams } from "react-router-dom";
import { useFetch } from "../services/useFetch";

export const Task7 = () => {
    const { id } = useParams();
    const { data, isLoading, isErrors } = useFetch(`https://jsonplaceholder.typicode.com/posts/${id}`)
    return (
        <>
            {isLoading && <div>Please waiting munites</div>}
            <h1>
                detail posts - {id}
                <div>
                    {data.title}
                </div>
            </h1>
        </>
    )
}
