/**
 * Task 5: react useEffect hook
 * th2: useEffect(callback,[])
 */
import { useEffect, useState } from "react"
export const Task6 = () => {
    const [posts, setpost] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    useEffect(() => {
        fetch('https://jsonplaceholder.typicode.com/posts')
            .then(res => res.json())
        setIsLoading(true)
            .then(posts => {
                setpost(posts);
            });
    }, []);
    return (
        <>
            <hr />
            <ul>
                {isLoading && <div>Please waiting for munutes</div>}
                {posts.map(item => (
                    <li key={item.id}>
                        {item.title}
                    </li>
                ))}

            </ul>
        </>
    )
}
