import React, { useState, useEffect } from "react";
import { useFetch } from "../services/useFetch";

export const Task9 = () => {
  const { data, isErrors, isLoading } = useFetch(
    "https://jsonplaceholder.typicode.com/todos"
  );
  const [selectedValue, setSelectedValue] = useState("all");
  const [filteredData, setFilteredData] = useState([]);

  useEffect(() => {
    if (data) {
      if (selectedValue === "true") {
        setFilteredData(data.filter((item) => item.completed));
      } else if (selectedValue === "false") {
        setFilteredData(data.filter((item) => !item.completed));
      } else if (selectedValue === "all") {
        setFilteredData(data);
      }
    }
  }, [data, selectedValue]);

  const handleSelectChange = (event) => {
    setSelectedValue(event.target.value);
  };

  return (
    <>
      <select
        className="form-select mt-3 mb-2"
        value={selectedValue}
        onChange={handleSelectChange}
      >
        <option value="all">All</option>
        <option value="true">Completed</option>
        <option value="false">Not Completed</option>
      </select>
      {isLoading && <div>Please wait a moment</div>}
      {isErrors && <div>Server Error 500</div>}
      {filteredData &&
        filteredData.length > 0 &&
        filteredData.map((item) => (
          <p key={item.id}>
            TITLE: {item.title}{" "}
            {item.completed ? (
              <small className="text-info">isCompleted</small>
            ) : (
              <small className="text-danger">isNotCompleted</small>
            )}
          </p>
        ))}
    </>
  );
};
