/**
 * Task 5: react useEffect hook
 */
import { useEffect, useState } from "react"
export const Task5 = () => {
    const [title, setTitle] = useState('');
    const handleChange = (event) => {
        setTitle(event.target.value);
    }
    useEffect(() => {
        document.title = title
    })
    return (
        <>
            <hr />
            <input onChange={handleChange} />
            <hr />
        </>
    )
}
