    //             setpost(posts);
    //             setIsLoading(false);
    //         })
    //         .catch(error => {
    //             setIsError(true);
    //             setIsLoading(false);
    //         });

    // }, []);
    return (
        <>
            <hr />
            <ul>
                {isErrors && <div>Sever internal errors</div>}
                {isLoading && <div>Please waiting for munutes</div>}
            </ul>
        </>
    )
}
