/**
 * Task 5: react useEffect hook
 * th2: useEffect(callback,[])
 */
import { useEffect, useState } from "react"
export const Task6 = () => {
    const [posts, setpost] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [isErrors, setIsError] = useState(false);
    useEffect(() => {
        (
            async () => {
                try {
                    setIsLoading(true);
                    const res = await fetch('https://jsonplaceholder.typicode.com/posts')
                    if (!res.ok) { throw new Error(); }
                    const data = await res.json();
                    if (data.length > 0) {
                        setpost(data);
                        setIsLoading(false);
                    }
                } catch (error) {
                    setIsError(true);
                    setIsLoading(false);
                }
            }
        )()
    }, []);
    return (
        <>
            <hr />
            <ul>
                {isErrors && <div>Sever internal errors</div>}
                {isLoading && <div>Please waiting for munutes</div>}
                {console.log(posts)}
                {
                    posts.map((post) => {
                        <li key={post.id}>{post.title}</li>
                    })
                }
            </ul>
        </>
    )
}
